(window.webpackJsonp=window.webpackJsonp||[]).push([[188],{703:function(_,t,v){"use strict";v.r(t);var a=v(3),s=Object(a.a)({},(function(){var _=this,t=_.$createElement,v=_._self._c||t;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("2011 年的夏天，刚刚结束中考的我与 AMA 一起由重庆朝天门码头乘船溯流而下，至湖北宜昌而终。其中经过的最美的景色是三峡中的小三峡，但我当时却无暇欣赏美景，只因眼神不好，看不清导游所指的许多悬棺。懊恼之际，上岸后偶然在一博物馆的玻璃展览柜中遇到一本讲述悬棺的书：重庆出版社于 1992 年出版的《中国悬棺葬》，顿生好奇。但因玻璃之隔，无法阅读，更是恼怒。")]),_._v(" "),v("p",[_._v("十年之后，今日在 GZ 图书馆观看拉斐尔画展，偶得此书于顶楼的 GZ 人文馆，喜不自胜。等到翻开，发现其中有关于"),v("a",{attrs:{href:"#%E5%B7%9D%E4%B8%9C%E9%95%BF%E6%B1%9F%E4%B8%89%E5%B3%A1%E5%9C%B0%E5%8C%BA%E7%9A%84%E6%82%AC%E6%A3%BA%E8%91%AC"}},[_._v("三峡悬棺")]),_._v("（原书 88-89 页）与"),v("a",{attrs:{href:"#%E6%82%AC%E6%A3%BA%E8%91%AC%E6%A3%BA%E6%9C%A8%E7%9A%84%E5%8D%87%E7%BD%AE%E6%8A%80%E6%9C%AF"}},[_._v("悬棺升置技术")]),_._v("（原书 138-151 页），更是喜笑颜开。得意忘形之中，索性将两部分摘录如下：")]),_._v(" "),v("h2",{attrs:{id:"川东长江三峡地区的悬棺葬"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#川东长江三峡地区的悬棺葬"}},[_._v("#")]),_._v(" 川东长江三峡地区的悬棺葬")]),_._v(" "),v("p",[_._v("位于四川省东部的奉节、巫山和巫溪县，地处长江三峡地区。这一带水流湍急，悬崖重叠，山谷幽深，景色雄伟壮丽。长江两岸的陡崖绝壁为致密而又坚硬的石灰岩，这里广阔的崖石层理和众多的山崖罅（"),v("strong",[_._v("xià")]),_._v("）隙是悬棺葬依山傍水的理想葬地。")]),_._v(" "),v("p",[_._v("由于长江三峡高峻险要的特殊地理环境，悬棺葬遗迹至今保留较多，除四川南部宜宾地区的珙县、兴文县等地以外，四川长江三峡地区也是我国悬棺葬遗迹和实地保留最多的地区。")]),_._v(" "),v("p",[_._v("关于长江三峡地区悬棺葬的历史文献记载最早见于唐宋时期。唐代诗人孟郊在《峡哀》诗中就曾描述过长江三峡中的悬棺：")]),_._v(" "),v("div",{staticClass:"custom-block tip"},[v("p",{staticClass:"title"}),v("p",[_._v("三峡一线天，三峡万绳泉。"),v("br"),_._v("\n上仄碎日月，下掣狂漪涟。"),v("br"),_._v("\n破魂一两点，凝幽数百年。"),v("br"),_._v("\n峡晖不停午，峡险多饥涎。"),v("br"),_._v("\n树根锁枯棺，孤骨袅袅悬。")])]),v("p",[_._v("四川长江三峡地区的悬棺葬主要分布在奉节，巫山、巫溪县等地。")]),_._v(" "),v("h3",{attrs:{id:"奉节县"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#奉节县"}},[_._v("#")]),_._v(" 奉节县")]),_._v(" "),v("p",[_._v("奉节县的悬棺葬主要分布在瞿塘峡的长江两岸。")]),_._v(" "),v("p",[_._v("《太平御览》卷 559《神怪志》："),v("br"),_._v("\n“唐将王果，舟经三峡，望见悬岩间有物似棺。令人视之，果棺也，骸骨存焉。”")]),_._v(" "),v("p",[_._v("曹学佺《蜀中名胜记》卷 21:"),v("br"),_._v("\n“赤岬山。岩穴中露一厘，甚高不可升，相传乃古兵书厘。”")]),_._v(" "),v("p",[_._v("正德《夔州府志》卷 7《古迹》："),v("br"),_._v("\n“兵书厘。在瞿塘峡半，远视若露一厘，高悬不能至。”")]),_._v(" "),v("p",[_._v("清·许瑞曾《东还纪程》："),v("br"),_._v("\n“风箱峡，暴从军夔（"),v("strong",[_._v("kuí")]),_._v("）门时有风箱峡者，数仞绝壁中迭置木厘，如风箱者甚多，仰望色如朽木，较棺形则小，其景象颇相类也。”")]),_._v(" "),v("p",[_._v("光绪《奉节县志》卷 34《古迹》："),v("br"),_._v("\n“风箱峡。在瞿塘峡中赤岬山下，崖穴间高不可升，相传鲁班之风箱也，又云乃古兵书厘。”")]),_._v(" "),v("p",[_._v("瞿塘峡全长 18 公里，指从奉节白帝城东夔门至巫山大溪之间长江两岸的地段。实地调查资料表明，这一带曾有较多的悬棺葬分布。")]),_._v(" "),v("p",[_._v("赤岬山巍然屹立在白帝城东面的长江北岸，高约 200 多米，犹如扼守长江天险的大门，故称夔门，瞿塘峡因此又名夔峡。")]),_._v(" "),v("p",[_._v("风箱峡，位于白帝城东约 3 公里处，风箱峡内悬棺葬分布十分密集，它的得名便因两岸绝壁多垒垒悬棺，远望若重叠置放的风箱。")]),_._v(" "),v("p",[_._v("赤岬山半腰距长江水面 100 多米，距山顶约 70~80 米的岩壁上有一宽 3~5 米，高 10 多米的竖直裂隙旁边书有“风箱峡”三个大字，隙内岩壁上横有木桩，木桩上原重叠置放棺木多达 12 具。")]),_._v(" "),v("h2",{attrs:{id:"悬棺葬棺木的升置技术"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#悬棺葬棺木的升置技术"}},[_._v("#")]),_._v(" 悬棺葬棺木的升置技术")]),_._v(" "),v("p",[_._v("中国和东南亚的悬棺葬，一般都搁置在距水面数十米的陡崖峭壁之上，不少地方，如四川长江三峡地区奉节、巫山、巫溪、川南、湖南澧水沿岸的悬棺葬甚至有将棺木置放在距江（河）面 100 多米或 200 多米的崖洞之中。川南珙县麻塘坝老鹰岩的悬棺葬是在距地面近百米如刀削斧劈的笔直崖壁上打木桩架置了 10 具棺木，而在珙县曹云河北岸一处距河面六、七十米的悬崖峭壁上木桩架置的悬棺重重叠叠竟多达 30 具。面对这种奇异壮观的情景，人们真是百思而不得其解，除了赞叹我国古代劳动人民征服自然的惊人创造力之外，实难想象当时的人们如何才能将装有尸体和随葬物品，重达数百公斤的棺木搁置到百仞悬崖之上。")]),_._v(" "),v("p",[_._v("实际上通过查阅历史文献和实地考察，要弄清悬棺葬的搁置方法亦并非难事。")]),_._v(" "),v("h3",{attrs:{id:"一-悬棺葬升置技术的文献记载"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#一-悬棺葬升置技术的文献记载"}},[_._v("#")]),_._v(" (一）悬棺葬升置技术的文献记载")]),_._v(" "),v("p",[_._v("关于悬棺搁置方法历史文献记载很少，唐人张鷟《朝野佥载》：“五溪蛮，父母死，于村外搁其尸三年而葬……于临江高山半肋凿龛以葬之，"),v("strong",[_._v("自山上悬索下柩")]),_._v("……”")]),_._v(" "),v("p",[_._v("清王世正《陇蜀余闻》：“辰沅中，缘江峭壁，高可百丈。避兵者"),v("strong",[_._v("以修便系腰，从山巅缒下")]),_._v("……，"),v("strong",[_._v("遂于壁山凿孔")]),_._v("，"),v("strong",[_._v("以巨木贯之")]),_._v("，即于木上纵横架栏木盾轇轕（jiāo gé）飞檐，……”许瓒曾《东还纪程》：“常德倒水岩，石皆壁立水滨，逶迤高广，上凿石窦者十，下临绝壑，内一窦，藏木櫘五，旧传为沉香棺。土人云，"),v("strong",[_._v("水涨时健儿引絙而上")]),_._v("，棺朽遗蜕尚存。”")]),_._v(" "),v("p",[_._v("同治《龙山县志·古迹》：“龙山仙人版，……石山壁立，下临大江，高百余丈，山腰一洞，洞口横木匣，长数尺。相传乾隆初年因雨，"),v("strong",[_._v("有人至山顶缒而下")]),_._v("，视皆古篆文字……”")]),_._v(" "),v("p",[_._v("《西阳直隶州志》卷 3《地舆志》3《古迹》：“秀山县“藏书洞。县志：在县石堤河西岸峭壁千仞，中度红木柜，……乾隆三十二年巡检郭良相"),v("strong",[_._v("悬布缒人而下")]),_._v("，启视见蝌蚪文数部……”")]),_._v(" "),v("p",[_._v("咸丰《黔江县志》卷 1《山川》载："),v("br"),_._v("\n“泉门口柜子岩，两岸峡壁高百余丈，下有深潭可通小舟，而柜崖即临其上。崖中数厂，深广半亩极难到。自上缒絙而下，为崖檐阻碍，自下而上，并无藤树可攀，而采硝者矫健若猿狖，"),v("strong",[_._v("寻微磴仰附而上，依石系缆")]),_._v("，胆壮者援引疾登，始得至。”")]),_._v(" "),v("p",[_._v("上述文献记载虽然简略，但有的是当代人记当时发生的事情，如《朝野佥载》记五溪蛮行悬棺葬是“自山上悬索下柩”，这种搁置棺的方法是可信的。其余文献资料多是记后世的人们为探知悬棺葬究竟，亦多采取自山顶或山巅缒入洞中或者依石系缆由下而上，仰附攀登，这些记载对于现今探讨悬棺葬的置棺方法都有很大启发和较高的参考价值。")]),_._v(" "),v("h3",{attrs:{id:"二-悬棺的几种升置技术"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#二-悬棺的几种升置技术"}},[_._v("#")]),_._v(" (二）悬棺的几种升置技术")]),_._v(" "),v("h4",{attrs:{id:"_1-栈道法"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-栈道法"}},[_._v("#")]),_._v(" 1. 栈道法")]),_._v(" "),v("p",[_._v("在选择的悬棺葬崖壁距江面高度不大的情况下可采用此法。")]),_._v(" "),v("p",[_._v("这种方法在悬崖上凿孔打桩搁置棺木和人工凿穴置放棺木的地区，如四川珙县，兴文县和贵州东北的松桃等地比较常见。")]),_._v(" "),v("p",[_._v("四川南部珙县麻塘坝的悬棺葬置棺方式为天然岩穴（洞）式，人工凿穴式和悬崖木桩式三种，其中以悬崖木桩式（即在悬岩峭壁上凿孔椽木，架棺其上）为多。采取此种置棺方式的悬棺葬崖壁大都可以见到距地面 2~3 米的地方就有许许多多 15~20 厘米见方的小孔，这种小孔一直到距地面十几米或数十米架置棺木的崖壁周围都还可见到。")]),_._v(" "),v("p",[_._v("关于这种小孔的用途，据实地考察后推测，有的是作为提升悬棺的栈道，从距地面（原为螃蟹溪河面）几米的地方凿孔打桩，以架横木，将沉重的棺木层层送上去。有些距目前地面较高的方孔大概也是插入木桩，架上横木放置棺木所用，目前无论在低处或高处崖壁上的方孔内都可见到残存的木桩。")]),_._v(" "),v("p",[_._v("四川南部的兴文县玉秀河和德胜河两岸的悬棺葬几乎全是人工开凿横穴式，这种长方形的横穴，头大足小，长、高、纵深均以放置 1 具长约 2 米的棺木为限。从距河面几米或从崖前浮土堆积开始即见崖壁上有密密麻麻的小方孔，大小与四川珙县麻塘坝基本相同，一直到人工开凿的横穴周围为止，不少方孔内亦残存有木桩。")]),_._v(" "),v("p",[_._v("黔东北松桃仙人岭云落屯距松桃河面 20~30 米的绝壁上，有人工开凿的横穴式悬棺葬，其下有许多长、宽、深约 15 厘米的小方孔，这些小方孔一直开凿到搁置棺木的人工凿穴为止，它们的用途应与珙县、兴文县的相同，均是作为栈道升置悬棺所用。")]),_._v(" "),v("p",[_._v("在四川长江三峡瞿塘峡西口南岸的孟良梯现存 65 个方形凿孔，分三段“之”字形排列，转折处多一两个凿孔，在第三段中间可见残留的一根木桩。据实地测量，方孔约 28 厘米见方，32 厘米深，孔间隔约 2 米，显系架木设梯的遗迹。由此梯从江边登上崖顶，缘崖口过狮子岩即是盔甲洞上打有“牛鼻孔”的地方。梯子附近还有吊槽，粉壁等洞穴崖葬。林向先生推测，这种孟良梯的用途也是为了便于吊运棺木"),v("sup",{staticClass:"footnote-ref"},[v("a",{attrs:{href:"#fn1",id:"fnref1"}},[_._v("[1]")])]),_._v("。")]),_._v(" "),v("h4",{attrs:{id:"_2-自山上悬索下枢-法"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_2-自山上悬索下枢-法"}},[_._v("#")]),_._v(" 2. “自山上悬索下枢”法")]),_._v(" "),v("p",[_._v("据实地考察所知，中国南方各地悬棺葬的棺木，无论广西、四川南部，东部长江三峡，还是湖北、湖南、江西、福建的悬棺或者棺盖，棺身带柄，或者棺身、棺盖的头、尾两端均凿有几个方孔，以便系绳索作升降之用。这些棺木（包括棺身、棺盖）大多为中空直径在五、六十厘米以上的整木挖凿而成、放入尸体和随葬物品以后，一具棺木至少重达 200 公斤左右。譬如以长江三峡巫溪县荆竹坝的 18 号小型棺木为例，实称重 80 公斤，若连同 2 具尸体和随葬物品总重量绝不会低于 150 公斤。如果以 V 式棺的棺长 2 米，外椁内棺，再加尸体和随葬物品，总重量也许会达 500 公斤。据参加四川珙县麻塘坝僰人悬棺清理工作的人讲，在这里取下的棺木一般也重达 300 公斤左右。")]),_._v(" "),v("p",[_._v("面对挺拔峭立的山崖，下有湍急的河水，人们如何将这些沉重的棺木搁置到几十米乃至上百米的悬崖上去呢？根据实地考察所知，“自山上悬索下枢”是将悬棺置于高崖绝壁最简便易行，同时也是中国悬棺葬中普遍采用的置棺方法。")]),_._v(" "),v("p",[_._v("1983 年冬笔者到湘西泸溪、沅陵的沅江两岸实地考察悬棺葬，在江中乘船观看这一带悬棺的位置，大多在距水面数十米高的悬崖绝壁，当时无论如何也猜测不出古时的人们究竟是怎样将那些沉重的棺木搁置上去的。后来舍舟登陆才发现在江上观察到的那座壁立于水滨有悬棺葬遗存的悬崖顶上却是一片平坦宽阔的地方，在这座山崖后面大约步行半小时便到了人烟稠密，建有辛女庙的村子。")]),_._v(" "),v("p",[_._v("1984 年 10 月笔者到广西左江流域大新县五山乡考察仙岩悬棺葬，悬棺葬地位于章山屯背后一座高 300 多米的小山上，放置棺木的天然洞穴距山脚约 200 多米，该洞正面壁立陡峭，无法攀登，在当地老乡的引导下，从崖洞左侧依靠荆棘葛藤，如猿猴悬吊，攀援而上。上去以后发现置棺的崖洞两侧山坡比较宽阔平缓，由于崖洞正面太陡峭无路，不可能从上山时攀登的路线返回，于是便从该洞背面平缓的山坡下山返回村寨，当然耗费时间是从悬崖正面攀登所用时间的几倍。")]),_._v(" "),v("p",[_._v("1988 年 6 月笔者到湖南澧水流域考察悬棺葬，慈利县龙潭河镇何家村白崖壁的悬棺葬位于澧水支流里耳溪畔距河面约 150 米的崖缘上，从正面看这座山岩如刀削然，完全无法攀登，于是笔者在当地农民的带领下，从它对面的一座高 200 米左右的陡峭山崖仰附攀登亦终于到达放置棺木的崖巅上面，因山崖挺拔，从原路下山非常危险，于是又多花几小时绕道从山后返回村子。")]),_._v(" "),v("p",[_._v("目前人们所看到的各地悬棺葬都是在江河上乘船或在地面上从山崖下面观察到的，因是由低处向上仰望，所以只见到放置棺木的山崖高峻无比，如果不亲自上山实地考察一番，对山崖顶上和山背后的情景必定茫然无知。实际上那些拔地而起，突兀峭立的山崖顶上、背后及其两旁的地势均平缓、开阔，而且都通往人们居住的村寨，古代的人们从山崖背后或其侧面将几百公斤的棺木运至山顶并不十分困难，无须像某些现代人设想的那样得靠绞车，滑轮等机械进行提升。")]),_._v(" "),v("p",[_._v("如何将棺木从山上吊下至事先选择好的天然洞穴，或凿孔打入崖壁的木桩之上倒是比较困难的事，因为放置棺木的自然洞穴或者打了木桩的崖壁之上一般均有崖檐遮挡，这样才能避免雨淋，起到保护棺木和尸骸的作用。")]),_._v(" "),v("p",[_._v("据观察，如果崖檐宽度不大，人们可到崖洞口用铁钩之类的工具将棺木勾至洞口或木桩上，倘若崖檐较宽（有的甚至宽达 1 米多），从山上吊下来的棺木距洞口或崖壁较远，在不可能采用勾拉的办法将棺木搁到预定位置的情况下，聪明的人们仍有办法达到悬葬高崖绝壁的目的。此时人们将利用山崖的凹陷之处，或在崖檐下的崖壁上选择适当位置横向凿打一些脚窝，然后从山顶或崖壁凹处将固定好的绳索栓在腰间脚踩脚窝左右移动，慢慢将沉重的棺木移至洞口或搁置在悬崖木桩之上。例如四川珙县麻塘坝九盏灯的悬棺葬共有 15 具棺木，置棺高度在 25~50 米左右，5 具置于天然洞穴内，10 具搁置崖壁所凿的木桩之上，置棺的崖壁上方均有较宽的凸出崖檐遮挡，其下的崖壁上就有许多凹陷很深的脚窝。又如麻塘坝老鹰岩峭壁插入的木桩上重叠搁置了 10 具棺木，距地面 80 多米，其上崖檐较宽，但置放棺木的崖壁距山顶却较近，而且壁立的山崖中间有一条裂开很深的凹陷，人们亦可从岩隙凹陷之处由上而下吊运棺木，采取在岩壁上打脚窝的方法搁置棺木。由此可以联想到长江三峡等地区峭立几百米，甚至近千米的绝壁上置放的悬棺均可采取“自山上悬索下枢”的方式。")]),_._v(" "),v("p",[_._v("据林向先生实地考察，在四川长江三峡地区，凡有水平层理形成岩缝的地方均可沿岩缝横行。如巫溪县荆竹坝棺木岩虽高峻不可攀，但却有数道近乎水平的岩缝横贯两边山垭，如登上垭口，再沿岩缝横行，即可到达葬所。在有些原本难通的部位，有方形凿孔痕迹，似曾架栈道通过。据 1966 年爬上洛门峡者述说，当地岩上亦有石栈道可横行至置棺处，只是后来被破坏了"),v("sup",{staticClass:"footnote-ref"},[v("a",{attrs:{href:"#fn1",id:"fnref1:1"}},[_._v("[1:1]")])]),_._v("。")]),_._v(" "),v("p",[_._v("另据在奉节、巫山、巫溪等长江三峡地区和长江沿岸多年进行实地考察并多次参加过当地悬棺葬吊运取棺、清理的考古工作者提供的第一手资料，在从山顶上悬索下吊棺木的过程中，除了置棺的崖洞、崖墩、打桩的崖壁上方有宽大的崖檐遮挡外，还因崖壁都并非光滑平直，往往会碰到一些凸出的崖石，这崖石可使人们站立稳当和暂时停放棺木。人们在腰间系绳保证了安全的情况下，可利用这些崖石将棺木逐渐下移，直至送到预先选定的位置。")]),_._v(" "),v("p",[_._v("长江三峡及其附近长江沿岸地区的悬棺葬置棺的下方崖壁上，绝大多数未见脚窝、栈道的痕迹。实地考察所知，在棺木下吊时，除了凸出的崖石可利用之外，山崖上的一些斜坡也可利用。如四川省巫溪县“九层楼”的悬棺葬，棺木距江面 200 多米，置棺的崖墩上方有一宽大崖檐可遮挡风雨，崖墩距山崖顶部约三、四十米，万县地区的考古工作者在腰间系绳索，从崖顶缒下，打算到崖墩上开启棺木，作清理工作，但在距崖墩仅 10 余米的地方，任凭腰栓绳索或采用其他方法均不能到达。这一处悬棺费既无脚窝，亦无栈道痕迹，通过仔细观察，方才发现这里有一段斜坡可通至置棺的崖墩之上，由此便联想到古人在“自山上悬索下枢”时，也可利用崖上的斜坡将棺木滑下至事先选定的地点。")]),_._v(" "),v("h4",{attrs:{id:"_3-其他方法"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_3-其他方法"}},[_._v("#")]),_._v(" 3. 其他方法")]),_._v(" "),v("p",[_._v("除了栈升法和“自山上悬索下松”以外，用人力直接将棺木送到悬崖上似乎不太可能。但倘若悬棺葬具不用重达数百斤的棺木而是用布袋、麻袋、瓮袋之类殓装尸骸，依靠在岩上固定的绳索或者用竹木制成的长梯子人们可将其运送至事先选定好的天然洞穴或人工凿穴之中。比如菲律宾巴拉望岛悬棺葬中的瓮棺就是由一个人攀附从山上固定好的绳索送到崖洞内的。现代行悬棺葬的印度尼西亚托拉贾人，贫穷人家庭的 8 岁孩童死亡，悬葬时即使用布袋殓装，由两人搭木梯传送至人工凿穴之中"),v("sup",{staticClass:"footnote-ref"},[v("a",{attrs:{href:"#fn2",id:"fnref2"}},[_._v("[2]")])]),_._v("。")]),_._v(" "),v("h4",{attrs:{id:"_4-关于地貌变化在悬棺葬置棺方法研究中的地位"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_4-关于地貌变化在悬棺葬置棺方法研究中的地位"}},[_._v("#")]),_._v(" 4. 关于地貌变化在悬棺葬置棺方法研究中的地位")]),_._v(" "),v("p",[_._v("华南地区的悬棺葬年代如前所述，最早的福建武夷山可到商周时期，川、鄂、湘、黔几省交界的“五溪蛮”地和湘桂交界的湘江流域以及广西左右江流域的悬棺葬大都为唐宋时期的文化遗存，川南和滇东北的悬棺葬年代最晚，为明万历初年。由此可见，中国早期悬棺葬距今至少有两、三千年，一般都有七、八百年的历史，最晚的距今也在四百年以上。在这千百年中河道、地貌都可能发生较大变化。例如珙县麻塘坝在距今二百多年前的清朝乾隆时期还为南广河支流螃蟹溪的宽阔河床，不过，已水不盈尺了，后来河流干酒，河床及其周围平坦地区早已被垦殖开发成一片良田沃土，可以设想在四、五百年前川南僰人盛行悬棺葬的明代，螃蟹溪的水位肯定是比较高的，那么棺木置放的高度也不会像现在所看到的这样高峻。")]),_._v(" "),v("p",[_._v("河水在几百年之内可干涸，“沧海变桑田”。何况福建武夷山的九曲溪，江西贵溪县仙岩的信江支流上清河等江河两岸石壁，在两、三千年内都经历了河水对河床的冲刷、侵蚀，在长期自然力的作用下，河流水位降低是完全可能的事。加上千百年前人口稀少，土地垦殖面积不大，山区林木茂密，河流两岸的大树还可有助于人力由下向上搬运棺木。因此《试析仙水岩“悬棺之谜”及其地学价值》一文提出，由于地质变迁而导致目前所见到华南地区大多悬棺葬位置升高的观点，在悬棺葬研究中并不是没有多大意义，通过实地考察的例证说明，“地貌变迁”在揭开“悬棺之谜”方面有一定的科学道理。")]),_._v(" "),v("h4",{attrs:{id:"_5-关于机械技术升置悬棺的质疑"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_5-关于机械技术升置悬棺的质疑"}},[_._v("#")]),_._v(" 5. 关于机械技术升置悬棺的质疑")]),_._v(" "),v("p",[_._v("自从 1989 年 4 月以来，中国民族学会编《民族学通讯》、新华通讯社、《人民日报》、《江西文物》等报刊连续报道和刊登了美国加州圣地亚哥分校的美国学者和上海同济大学、江西等地的中国学者合作，采用绞车、滑轮等机械装置在江西贵溪仙岩把一具重约 150 公斤的“棺材”吊进了一个离上清河水面约 20 多米的悬崖洞穴中，声称“重现了二千多年古人吊装悬馆的壮观场面”，从而“解开了中国悬棺又一千古之谜”"),v("sup",{staticClass:"footnote-ref"},[v("a",{attrs:{href:"#fn3",id:"fnref3"}},[_._v("[3]")])]),_._v("。这一消息和有关论文的登载引起了新闻界和社会各界的广泛关注。")]),_._v(" "),v("p",[_._v("中国悬棺葬除了它的棺木大多置放在距水面（或原本是水面，由于自然变化和经济开发等原因水面变成了地面）数十米至上百米，甚至几百米的悬崖峭壁上，使人们感到不可思议而深感神秘而外，更使人们关切的是华南地区古代少数民族为何要悬葬高崖绝壁，究竟是出于一种什么样的宗教意识，具有如此强大的精神动力促使古人要采取这样奇异的葬尸方式？后者才是人们更感神秘莫测，使中外学者半个多世纪以来孜孜不倦进行探索的重要问题。")]),_._v(" "),v("p",[_._v("对于历史学家和人类学家来讲，要重现过去的历史场景，是相当困难的问题，因为历史学和人类学所研究的社会，都不是目前我们生活的社会。尤其是研究早已从中国南方大陆消失的少数民族的丧葬习俗，更不能用现代人和汉民族的立场、观点去加以解释，否则会“失之毫厘，差之千里。”")]),_._v(" "),v("p",[_._v("中国悬棺葬绝大多数在临江面水的高崖绝壁之上。例如长江三峡一带江水湍急，山崖挺拔峭立，在这种情况下要想采用栈道或诸如绞车、滑轮之类的提升技术，将数百公斤重的棺木升至几百米甚至近千米的悬崖峭壁，实在是难以想象的事。而且为了提升一具棺木到达三、四十米的高度，若使用绞车、滑轮的机械方法还须事先设计出十数种提升方案，然后从中筛选出一种最佳方案，进行实地操作。如此繁琐，复杂的悬棺升置技术，大约也脱离千百年前的实际情况。")]),_._v(" "),v("p",[_._v("历史文献记载和考古学、民族学的调查资料表明，分布于中国长江流域及其以南广大地区的悬棺葬是古代少数民族的文化遗存，而不是汉族的丧葬习俗，与汉族文化具有很大的差异。自古以来，我国少数民族与汉族在生产技术、经济、文化和其他社会发展方面很不平衡。汉族社会自周秦以来便进入了封建社会的发展阶段，到了明代已有资本主义因素的萌芽，然而我国许多少数民族直到 20 世纪 50 年代初期尚处于原始社会发展阶段末期，而且华南一些少数民族的社会发展更为后进。甚至到了 90 年代的今天，许多先进科学技术已经传入到民族地区，南方仍有一部分少数民族对机械化的工具感到陌生。衣食住行等日常生产、生活中所需的一切，几乎全使用手工操作。在黔桂交界的石灰岩山区生活的瑶族，不久之前尚盛行崖洞葬，现改为土葬，他们常常是数百上千的人不惜耗费大半天的时间，翻山越岭，将死者的殓尸棺木送到本氏族或家族的公共墓地。")]),_._v(" "),v("p",[_._v("悬棺葬的棺木升置技术有许多种，“在可以使用的数种方法中，古人会尽量选用最为简便易行的方法。实际上原始的方法都很简便，因此愈简便的方法愈合理。”"),v("sup",{staticClass:"footnote-ref"},[v("a",{attrs:{href:"#fn4",id:"fnref4"}},[_._v("[4]")])]),_._v("在科学技术和生产力都不发达的古代社会，人们会尽最大努力去寻找简便易行的方法。")]),_._v(" "),v("p",[_._v("如前所述，实地考察所获的大量第一手资料说明，华南地区的悬棺葬大多采用“自山上悬索下松”的方法。这种方法省时省力，简便易行，不需搭十几米或数十米的脚手架，更不需去制造绞车、滑轮等机械工具，绞尽脑汁去设计数十种提升方案。由此而论，“自山上悬索下板”乃是悬棺葬中许多种置馆方法中最简便易行的一种，因而也是十分合理的一种。")]),_._v(" "),v("p",[_._v("国内外丰富的民族学资料，都给予了我们许多有益的启示。古代的人们在某个专门领域内所具备的聪明才智，说不定现代文明人还不具备呢！中国悬棺葬中采用的“自山上悬索下枢”的置馆技术大概便属于这种情况。")]),_._v(" "),v("p",[_._v("认为用绞车、滑轮吊运馆木是“解开了中国悬棺葬这一千古之谜”，试图从科技的角度探讨悬棺葬的奥秘，这是一个大胆而又可喜的尝试，但由于脱离了千百年前的时代和华南行悬棺葬民族的社会历史背景，缺乏有力证据，因此难以令人信服。")]),_._v(" "),v("p",[_._v("中国悬棺葬虽然表现为一种丧葬方式，但这种方式的背后却与考古学、民族学、民俗学、宗教学等等多种学科密切相关。“中国悬棺千古之谜”应该是指它在上述多种学科方面所具有的更加深层的文化内涵。")]),_._v(" "),v("p",[_._v("丧葬习俗是人类社会文化的一个侧面，通过对悬棺葬的研究，可以从侧面复原它所反映的宗教观念、行悬棺葬民族的心理素质和当时的社会组织、社会经济生活等等。")]),_._v(" "),v("p",[_._v("广泛分布于中国南方和东南亚地区的悬棺葬，是丧辈习俗，也是考古文化遗存。对于它的年代、族属以及行悬棺葬的各民族之间、古代民族与现代民族之间的相互关系等等，是悬棺葬研究中不可回避的问题，这就涉及到考古学、民族学、民族的来源和民族迁徒等民族历史及其民族关系史的一系列重大问题。")]),_._v(" "),v("p",[_._v("悬棺葬既是一种葬俗，那么还须从民俗的角度探讨它的起源、社会功能、社会影响及其意义。所有这一切才是“中国悬棺之谜”的重要内容，而悬馆的升置技术则只是其中一个很次要的方面。倘若仅从悬棺葬中若干种置馆方法中的一种去加以探讨、解释，便认为“解开了中国悬棺葬这一千古之谜”，未免将“中国悬馆之谜“过于简单化了。")]),_._v(" "),v("p",[_._v("半个多世纪以来，许多中外学者为解开“中国悬棺之谜”付出了艰巨的劳动，但到目前为止，我们对悬棺葬只能描述它，还没有完全理解它，距彻底揭开“中国悬棺之谜”还有一段路程，这需要历史学、民族学、宗教学、民俗学、体质人类学等诸多学科的密切配合，共同努力，作更进一步探讨，方能达到目的。")]),_._v(" "),v("h2",{attrs:{id:"coda"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#coda"}},[_._v("#")]),_._v(" Coda")]),_._v(" "),v("p",[_._v("至此，我心中对悬棺升置技术的疑惑已几乎完全解决，畅快之余却有些许失落，只因真相并非想象之中的绚丽夺目、天马行空。终了，附上本书扉页作者陈明芳赠与其导师的题词：")]),_._v(" "),v("img",{staticClass:"medium-zoom",attrs:{src:"/academic_inheritance.png",alt:"Academic Inheritance",width:"50%"}}),_._v(" "),v("p",[_._v("真诚祝愿中国有更多学术严谨、好奇未知、传承真相的科研工作者！")]),_._v(" "),v("h2",{attrs:{id:"注释"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#注释"}},[_._v("#")]),_._v(" 注释")]),_._v(" "),v("hr",{staticClass:"footnotes-sep"}),_._v(" "),v("section",{staticClass:"footnotes"},[v("ol",{staticClass:"footnotes-list"},[v("li",{staticClass:"footnote-item",attrs:{id:"fn1"}},[v("p",[_._v("林向：《川东峡江地区的崖葬》，《民族学研究》第四辑。 "),v("a",{staticClass:"footnote-backref",attrs:{href:"#fnref1"}},[_._v("↩︎")]),_._v(" "),v("a",{staticClass:"footnote-backref",attrs:{href:"#fnref1:1"}},[_._v("↩︎")])])]),_._v(" "),v("li",{staticClass:"footnote-item",attrs:{id:"fn2"}},[v("p",[_._v("凌纯声：《中国与东南亚之崖葬文化》。 "),v("a",{staticClass:"footnote-backref",attrs:{href:"#fnref2"}},[_._v("↩︎")])])]),_._v(" "),v("li",{staticClass:"footnote-item",attrs:{id:"fn3"}},[v("p",[_._v("《千古之谜被解开——中国悬馆仿古试吊成功》，中国民族学会编《民族学通讯》第 90 期，1989 年 10 月 15 日。陆敬严、[美]程贞一：《中国悬棺升置技术研究》。曲利平：《贵溪悬棺吊装实施过程》。均载《江西文物》1991 年第 1 期。 "),v("a",{staticClass:"footnote-backref",attrs:{href:"#fnref3"}},[_._v("↩︎")])])]),_._v(" "),v("li",{staticClass:"footnote-item",attrs:{id:"fn4"}},[v("p",[_._v("陆敬严，[美]程贞一：《中国悬棺升置技术研究》，《江西文物》1991 年第 1 期。 "),v("a",{staticClass:"footnote-backref",attrs:{href:"#fnref4"}},[_._v("↩︎")])])])])])])}),[],!1,null,null,null);t.default=s.exports}}]);